CREATE TABLE IF NOT EXISTS Deck (
    id integer PRIMARY KEY,
    suit text NOT NULL,
    number text NOT NULL,
    description text NOT NULL,
    img text
);

CREATE TABLE IF NOT EXISTS Players (
    id integer PRIMARY KEY,
    user text NOT NULL,
    active boolean NOT NULL,
    cards text
);

CREATE TABLE IF NOT EXISTS Table (
    id integer PRIMARY KEY,
    players text,
    ingame text
);
